window.onload=function(){for(var e=document.querySelectorAll(".add"),n=document.querySelector(".count-clear"),t=0;t<e.length;t++){e[t].addEventListener("click",function(e){e.preventDefault();var n=+this.previousElementSibling.innerHTML.match(/[0-9]/g).join(""),t=document.querySelector(".counter"),r=t.innerHTML;t.innerHTML=+r+n})}n.addEventListener("click",function(e){e.preventDefault(),document.querySelector(".counter").innerHTML="0"})};